import React from 'react';
import axios from 'axios'
import {connect} from 'react-redux';
import {setFullArrayOfProducts,setDisplayedArrayOfProducts } from './redux/Shop/Shop.actions'
class App extends React.Component{
  componentDidMount(){
    axios.get('http://localhost:5000/products').then((response)=>{
    this.props.setFullArrayOfProducts(response.data);
    this.props.setDisplayedArrayOfProducts(response.data.slice(0,20));

    console.log(response.data);
    })
  }
  render(){
  return(
    <div>Hello Products
      {this.props.DisplayedProductsArray.map((Product)=>{

        return(
          <div
          style={{fontSize:Product.size}}
          id={Product.id}>
            {Product.face}
              {Product.price}
          </div>
        )
      })}
    
    </div>
  )
  }
}
const mapStateToProps=state=>({
  DisplayedProductsArray:state.shop.DisplayedProductsArray
})
const mapDispatchToProps = (dispatch) => ({
  setFullArrayOfProducts: (FullArrayOfProducts) => dispatch(setFullArrayOfProducts(FullArrayOfProducts)),
  setDisplayedArrayOfProducts:(DisplayedArrayOfProducts)=>dispatch(setDisplayedArrayOfProducts(DisplayedArrayOfProducts))
});

export default connect(mapStateToProps,mapDispatchToProps)(App);
