import React from "react";
import "./Checkout.css";
import { connect } from "react-redux";
const CheckoutPage = ({ ItemsOfCard }) => (
  <div>
    {" "}
    <div className="cart-items">
      {ItemsOfCard.length ? (
        ItemsOfCard.map((cartItem) => (
          <div className="cart-item" key={cartItem.product.id}>
            <div className="item-details">
              <span className="Cardface">{cartItem.product.face}</span>
              <span className="CardPrice">
                <span className="CardDropdownArrows"> &lt; </span>
                {cartItem.value}
                <span className="CardDropdownArrows"> &gt; </span> x
                <span style={{ color: "green" }}> $ </span>{" "}
                {cartItem.product.price}
              </span>
              <span className="CartDropdownX">&#10006;</span>
            </div>
          </div>
        ))
      ) : (
        <span className="empty-message">Your cart is empty</span>
      )}
    </div>
  </div>
);
const mapStateToProps = (state) => ({
  ItemsOfCard: state.shop.ItemsOfCard,
});
export default connect(mapStateToProps)(CheckoutPage);
