import { ShopActionTypes } from "./Shop.types";
const INITIAL_STATE = {
  FullArray:[],
  ArrayUsed:[]
};
const ShopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {

    case ShopActionTypes.SET_DISIPLAYED_ARRAY:
      return {
        ...state,
        ArrayUsed:action.payload
      }
      case ShopActionTypes.SET_FULL_ARRAY:
        return {
          ...state,
          FullArray:action.payload
        }
    default:
      return state;
  }
};
export default ShopReducer;
