import React from "react";
import "./Checkout.css";
import { connect } from "react-redux";
const FindTotal = (Array) => {
  let sum = 0;
  for (let i = 0; i < Array.length; i++) {
    sum += Array[i].product.price * Array[i].value;
  }
  return sum;
};
const CheckoutPage = ({ ItemsOfCard }) => (
  <div className="CheckoutPage">
    {ItemsOfCard.length ? (
      ItemsOfCard.map((cartItem) => (
        <div className="CheckoutPageItem" key={cartItem.product.id}>
          <span className="CheckoutPageface">{cartItem.product.face}</span>
          <div className="CheckoutPageValue">
            <span className="CheckoutPageArrows">+</span>
            {cartItem.value}
            <span className="CheckoutPageArrows">-</span>
          </div>
          <div className="CheckoutPagePrice">
            <span style={{ color: "green" }}> $ </span> {cartItem.product.price}
          </div>
          <span className="CheckoutPageX">&#10006;</span>
        </div>
      ))
    ) : (
      <span className="">Your cart is empty</span>
    )}
    {ItemsOfCard.length ? (
      <div>
        {" "}
        <span style={{ color: "green" }}> $ </span> {FindTotal(ItemsOfCard)}
      </div>
    ) : null}
  </div>
);
const mapStateToProps = (state) => ({
  ItemsOfCard: state.shop.ItemsOfCard,
});
export default connect(mapStateToProps)(CheckoutPage);
