import React, { useState } from "react";
import "./Filter.styles.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSortUp } from "@fortawesome/free-solid-svg-icons";
import { faSortDown } from "@fortawesome/free-solid-svg-icons";
import { faFilter } from "@fortawesome/free-solid-svg-icons";
import Sort from "../Sort/Sort.component";

const Filter = () => {
  const [FilterState, setFilterState] = useState(false);
  const [SizeSort, setSizeSort] = useState(false);

  const [PrizeSort, setPrizeSort] = useState(false);
  const [IdSort, setIdSort] = useState(false);

  return (
    <div>
      {FilterState === false ? (
        <div className="Filtering" onClick={() => setFilterState(!FilterState)}>
          Filters
          <FontAwesomeIcon
            icon={faFilter}
            className="icon"
            color="#413b3b"
          ></FontAwesomeIcon>
        </div>
      ) : (
        <div>
          <div
            className="Filtering"
            onClick={() => setFilterState(!FilterState)}
          >
            Filters
            <FontAwesomeIcon
              icon={faFilter}
              className="icon"
              color="#413b3b"
            ></FontAwesomeIcon>
          </div>
          <div className="SortOption">
            <div style={{ fontSize: "1.4vw", margin: "1vh 1vw" }}>
              {" "}
              <b>Sort by: </b>
            </div>
            <Sort
              title="Id"
              sortVisibility={IdSort}
              onClickOfSort={() => {
                setSizeSort(false);
                setPrizeSort(false);
                setIdSort(true);
              }}
            />{" "}
            <Sort
              title="Prize"
              sortVisibility={PrizeSort}
              onClickOfSort={() => {
                setSizeSort(false);
                setPrizeSort(true);
                setIdSort(false);
              }}
            />
            <Sort
              title="Sort"
              sortVisibility={SizeSort}
              onClickOfSort={() => {
                setSizeSort(true);
                setPrizeSort(false);
                setIdSort(false);
              }}
            />{" "}
          </div>
        </div>
      )}
    </div>
  );
};
export default Filter;
