import React, { useEffect } from "react";
import "./ProductPaginationView.styles.css";
import { connect } from "react-redux";
import ProductCard from "../Product/Product.component";
import Pagination from "../Pagination/Pagination.component";
import { toggleSpinner } from "../../redux/Spinner/Spinner.actions";
import Spinner from "../Spinner/spinner.component";
import {
  setDisplayedArrayOfProducts,
  setValueByName,
} from "../../redux/Shop/Shop.actions";
import { getData, ContrFunc } from "../../api/AxiosHelperFunctions";
const EncreaseOrDecreasePage = async (
  increment,
  clickedPage,
  totalPages,
  setValueByName,
  toggleSpinner,
  AddsIndexes
) => {
  const PageClickedAfterPressing =
    clickedPage + increment <= 0
      ? 0
      : clickedPage + increment >= totalPages
      ? totalPages
      : clickedPage + increment;
  await setValueByName({
    name: "clickedPage",
    value: PageClickedAfterPressing,
  });
  let AddsIndexesToModify = AddsIndexes;
  AddsIndexesToModify[PageClickedAfterPressing - 1] = findRandomNotFirstAdd(
    AddsIndexes,
    PageClickedAfterPressing - 2
  );
  await setValueByName({
    name: "AddsIndexes",
    value: [...AddsIndexesToModify],
  });

  getProductsNedded(PageClickedAfterPressing, toggleSpinner, setValueByName);
};
const OnPageClick = async (
  page,
  setValueByName,
  toggleSpinner,
  AddsIndexes
) => {
  await setValueByName({
    name: "clickedPage",
    value: page,
  });
  let AddsIndexesToModify = AddsIndexes;
  AddsIndexesToModify[page - 1] = findRandomNotFirstAdd(AddsIndexes, page - 2);
  await setValueByName({
    name: "AddsIndexes",
    value: [...AddsIndexesToModify],
  });
  getProductsNedded(page, toggleSpinner, setValueByName);
};
const getProductsNedded = async (
  clickedPage,
  toggleSpinner,
  setValueByName
) => {
  await getData(ContrFunc.Functions.products, toggleSpinner, {
    _page: clickedPage,
    _limit: 20,
  }).then((response) => {
    setValueByName({
      name: "PaginationDisplayedArrayOfProducts",
      value: response.data,
    });
  });
  toggleSpinner();
};
const findRandomNotFirstAdd = (AddsIndexes, index) => {
  let zIndex = Math.floor(Math.random() * 1000);
  while (zIndex === AddsIndexes[index]) {
    zIndex = Math.floor(Math.random() * 1000);
  }
  return zIndex;
};
const ProducdPaginationView = ({
  ProductsFullArray,
  clickedPage,
  setValueByName,
  toggleSpinner,
  PaginationDisplayedArrayOfProducts,
  SpinnerVissibility,
  AddsIndexes,
}) => {
  useEffect(() => {
    getProductsNedded(1, toggleSpinner, setValueByName);
  }, [toggleSpinner, setValueByName]);

  return (
    <div className="ShowPageProductsPagination">
      <div
        style={{
          position: "relative",
          width: "50vw",
          height: "15vh",
          border: "1px solid black",
        }}
      >
        <img
          style={{ position: "relative", width: "90vw", height: "15vh" }}
          className="advertisment"
          alt="Loading"
          src={`http://localhost:5000/ads?r=${AddsIndexes[clickedPage - 1]}`}
        />
      </div>

      <div className="Products" style={{ width: "90vw", height: "65vh" }}>
        {SpinnerVissibility === true ? (
          <Spinner></Spinner>
        ) : (
          PaginationDisplayedArrayOfProducts.map((Product) => {
            return (
              <ProductCard
                key={Product.id}
                id={Product.id}
                price={Product.price}
                size={Product.size}
                face={Product.face}
                date={Product.date}
              />
            );
          })
        )}
      </div>

      <Pagination
        style={{ width: "90vw", height: "10vh" }}
        pages={
          ProductsFullArray.length % 20 === 0
            ? ProductsFullArray.length / 20
            : ProductsFullArray.length / 20 + 1
        }
        clickedPage={clickedPage}
        onClickOfPage={(page) => {
          OnPageClick(page, setValueByName, toggleSpinner, AddsIndexes);
        }}
        IncreasePageByThree={() => {
          EncreaseOrDecreasePage(
            3,
            clickedPage,
            ProductsFullArray.length % 20 === 0
              ? ProductsFullArray.length / 20
              : ProductsFullArray.length / 20 + 1,
            setValueByName,
            toggleSpinner,
            AddsIndexes
          );
        }}
        IncreasePageByOne={() => {
          EncreaseOrDecreasePage(
            1,
            clickedPage,
            ProductsFullArray.length % 20 === 0
              ? ProductsFullArray.length / 20
              : ProductsFullArray.length / 20 + 1,
            setValueByName,
            toggleSpinner,
            AddsIndexes
          );
        }}
        DecreasePageByOne={() => {
          EncreaseOrDecreasePage(
            -1,
            clickedPage,
            ProductsFullArray.length % 20 === 0
              ? ProductsFullArray.length / 20
              : ProductsFullArray.length / 20 + 1,
            setValueByName,
            toggleSpinner,
            AddsIndexes
          );
        }}
        DecreasePageByThree={() => {
          EncreaseOrDecreasePage(
            -3,
            clickedPage,
            ProductsFullArray.length % 20 === 0
              ? ProductsFullArray.length / 20
              : ProductsFullArray.length / 20 + 1,
            setValueByName,
            toggleSpinner,
            AddsIndexes
          );
        }}
      />
    </div>
  );
};
const mapStateToProps = (state) => ({
  DisplayedProductsArray: state.shop.DisplayedProductsArray,
  ProductsFullArray: state.shop.ProductsFullArray,
  clickedPage: state.shop.clickedPage,
  SpinnerVissibility: state.spinner.SpinnerVissibility,
  AddsIndexes: state.shop.AddsIndexes,
  Index: state.shop.Index,
  LoadingMore: state.shop.LoadingMore,
  PaginationDisplayedArrayOfProducts:
    state.shop.PaginationDisplayedArrayOfProducts,
});
const mapDispatchToProps = (dispatch) => ({
  setDisplayedArrayOfProducts: (DisplayedArrayOfProducts) =>
    dispatch(setDisplayedArrayOfProducts(DisplayedArrayOfProducts)),
  setValueByName: (NameValue) => dispatch(setValueByName(NameValue)),
  toggleSpinner: () => dispatch(toggleSpinner()),
});
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ProducdPaginationView);
