import React from "react";
import "./ProductPaginationView.styles.css";
import { connect } from "react-redux";
import ProductCard from "../Product/Product.component";
import {
  setDisplayedArrayOfProducts,
  setValueByName,
} from "../../redux/Shop/Shop.actions";

const ProducdPaginationView = ({
  ProductsFullArray,
  clickedPage,
  setValueByName,
}) => {
  const pages = ProductsFullArray.length / 10;
  let nrofpages = [];
  for (let i = 0; i < pages; i++) {
    nrofpages.push(i);
  }
  let pageCount = 0;
  console.log(nrofpages);
  return (
    <div className="pagination">
      <div className="Paginationpages ">&laquo;</div>

      {nrofpages.map((page) => {
        pageCount++;
        {
          return pageCount >= 5 + clickedPage && pageCount <= 7 ? (
            <div className="Paginationpages " id={page}>
              .
            </div>
          ) : pageCount <= 4 + clickedPage && pageCount > clickedPage ? (
            <div
              className="Paginationpages Numbers"
              id={page}
              onClick={() => {
                setValueByName({
                  name: "clickedPage",
                  value: page,
                });
              }}
            >
              {page}
            </div>
          ) : pageCount + 3 >= nrofpages.length ? (
            <div
              className="Paginationpages Numbers"
              id={page}
              onClick={() => {
                setValueByName({
                  name: "clickedPage",
                  value: page,
                });
              }}
            >
              {page}
            </div>
          ) : null;
        }
      })}

      <div className="Paginationpages">&raquo;</div>
    </div>
  );
};
const mapStateToProps = (state) => ({
  DisplayedProductsArray: state.shop.DisplayedProductsArray,
  ProductsFullArray: state.shop.ProductsFullArray,
  clickedPage: state.shop.clickedPage,
  SpinnerVissibility: state.spinner.SpinnerVissibility,
  AddsIndexes: state.shop.AddsIndexes,
  Index: state.shop.Index,
  LoadingMore: state.shop.LoadingMore,
});
const mapDispatchToProps = (dispatch) => ({
  setDisplayedArrayOfProducts: (DisplayedArrayOfProducts) =>
    dispatch(setDisplayedArrayOfProducts(DisplayedArrayOfProducts)),
  setValueByName: (NameValue) => dispatch(setValueByName(NameValue)),
});
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ProducdPaginationView);
