import { ShopActionTypes } from "./Shop.types";

export const setFullArrayOfProducts = (FullArrayOfProducts) => ({
  type: ShopActionTypes.SET_FULL_ARRAY_OF_PRODUCTS,
  payload: FullArrayOfProducts,
});
export const setDisplayedArrayOfProducts = (DisplayedArrayOfProducts) => ({
  type: ShopActionTypes.SET_DISIPLAYED_ARRAY_OF_PRODUCTS,
  payload: DisplayedArrayOfProducts,
});
export const setValueByName = (NameValue) => ({
  type: ShopActionTypes.SET_VALUE_BY_NAME,
  payload: NameValue,
});
export const increaseValueOfProduct = (ProductId) => ({
  type: ShopActionTypes.INCREASE_VALUE_OF_PRODUCT_BY_ONE,
  payload: ProductId,
});
export const decreaseValueOfProduct = (ProductId) => ({
  type: ShopActionTypes.DECREASE_VALUE_OF_PRODUCT_BY_ONE,
  payload: ProductId,
});
export const deleteProduct = (ProductId) => ({
  type: ShopActionTypes.DELETE_PRODUCT,
  payload: ProductId,
});
export const selectOneProduct = (ProductId) => ({
  type: ShopActionTypes.SELECT_ONE_PRODUCT,
  payload: ProductId,
});
export const selectOrDeselectAll = (ProductId) => ({
  type: ShopActionTypes.SELECT_ALL_OR_DESELECT_ALL,
});
