import AxiosCall from "./AxiosCall";
export const ContrFunc = {
  Controllers: {},
  Functions: {
    products: "products",
    ads: "ads",
  },
};

export const getData = async (functionname, spinnerFunct, parameters) => {
  if (spinnerFunct !== null) {
    spinnerFunct();
  }
  let response = await AxiosCall.get(
    `/${functionname}`,
    parameters
      ? {
          params: {
            ...parameters,
          },
        }
      : null
  );
  return response;
};
