import { combineReducers } from "redux";

import ShopReducer from "./Shop/Shop.reducer";
import SpinnerReducer from "./Spinner/Spinner.reducer";
export default combineReducers({
  shop: ShopReducer,
  spinner: SpinnerReducer,
});
