import { ShopActionTypes } from "./Shop.types";
import {
  FindItemToArrayByIdIncreaseOrDecrease,
  RemoveProductById,
  SelectAll,
  DeSelectAll,
  SelectOneById,
} from "./Shop.filterhelper";
const INITIAL_STATE = {
  ProductsFullArray: [],
  DisplayedProductsArray: [],
  LoadingMore: false,
  Index: 0,
  AddsIndexes: [Math.floor(Math.random() * 1000)],
  clickedPage: 1,
  PaginationDisplayedArrayOfProducts: [],
  ItemsOfCard: [],
  SortBy: "Id",
};

const ShopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ShopActionTypes.INCREASE_VALUE_OF_PRODUCT_BY_ONE:
      return {
        ...state,
        ItemsOfCard: [
          ...FindItemToArrayByIdIncreaseOrDecrease(
            action.payload,
            state.ItemsOfCard,
            1
          ),
        ],
      };
    case ShopActionTypes.DECREASE_VALUE_OF_PRODUCT_BY_ONE:
      return {
        ...state,
        ItemsOfCard: [
          ...FindItemToArrayByIdIncreaseOrDecrease(
            action.payload,
            state.ItemsOfCard,
            -1
          ),
        ],
      };
    case ShopActionTypes.DELETE_PRODUCT:
      return {
        ...state,
        ItemsOfCard: [...RemoveProductById(action.payload, state.ItemsOfCard)],
      };
    case ShopActionTypes.SET_DISIPLAYED_ARRAY_OF_PRODUCTS:
      return {
        ...state,
        DisplayedProductsArray: action.payload,
      };
    case ShopActionTypes.SET_VALUE_BY_NAME:
      return {
        ...state,
        [action.payload.name]: action.payload.value,
      };
    case ShopActionTypes.SET_FULL_ARRAY_OF_PRODUCTS:
      return {
        ...state,
        ProductsFullArray: action.payload,
      };
    default:
      return state;
  }
};
export default ShopReducer;
