import { CartActionTypes } from "./Cart.types";
import {
  FindItemToArrayByIdIncreaseOrDecrease,
  RemoveProductById,
  SelectAllOrDeselectAll,
  SelectOneById,
} from "./Cart.filterhelper";
const INITIAL_STATE = {
  ItemsOfCard: [],
};

const CartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ShopActionTypes.SELECT_ALL_OR_DESELECT_ALL:
      return {
        ...state,
        ItemsOfCard: [...SelectAllOrDeselectAll(state.ItemsOfCard)],
      };

    case ShopActionTypes.SELECT_ONE_PRODUCT:
      return {
        ...state,
        ItemsOfCard: [...SelectOneById(action.payload, state.ItemsOfCard)],
      };

    case ShopActionTypes.INCREASE_VALUE_OF_PRODUCT_BY_ONE:
      return {
        ...state,
        ItemsOfCard: [
          ...FindItemToArrayByIdIncreaseOrDecrease(
            action.payload,
            state.ItemsOfCard,
            1
          ),
        ],
      };
    case ShopActionTypes.DECREASE_VALUE_OF_PRODUCT_BY_ONE:
      return {
        ...state,
        ItemsOfCard: [
          ...FindItemToArrayByIdIncreaseOrDecrease(
            action.payload,
            state.ItemsOfCard,
            -1
          ),
        ],
      };
    case ShopActionTypes.DELETE_PRODUCT:
      return {
        ...state,
        ItemsOfCard: [...RemoveProductById(action.payload, state.ItemsOfCard)],
      };

    case ShopActionTypes.SET_VALUE_BY_NAME_CART:
      return {
        ...state,
        [action.payload.name]: action.payload.value,
      };

    default:
      return state;
  }
};
export default CartReducer;
