import React from "react";
import "./Checkout.css";
import { connect } from "react-redux";
const FindTotal = (Array) => {
  let sum = 0;
  for (let i = 0; i < Array.length; i++) {
    sum += Array[i].product.price * Array[i].value;
  }
  return sum;
};
const CheckoutPage = ({ ItemsOfCard }) => (
  <div className="cart-items">
    {ItemsOfCard.length ? (
      ItemsOfCard.map((cartItem) => (
        <div className="" key={cartItem.product.id}>
          <div className="">
            <span className="">{cartItem.product.face}</span>
            <span className="">
              <span className=""> &lt; </span>
              {cartItem.value}
              <span className=""> &gt; </span> x
              <span style={{ color: "green" }}> $ </span>{" "}
              {cartItem.product.price}
            </span>
            <span className="">&#10006;</span>
          </div>
        </div>
      ))
    ) : (
      <span className="">Your cart is empty</span>
    )}
    {ItemsOfCard.length ? (
      <div>
        {" "}
        <span style={{ color: "green" }}> $ </span> {FindTotal(ItemsOfCard)}
      </div>
    ) : null}
  </div>
);
const mapStateToProps = (state) => ({
  ItemsOfCard: state.shop.ItemsOfCard,
});
export default connect(mapStateToProps)(CheckoutPage);
