import React from "react";
import axios from "axios";
import "./App.css";
import { connect } from "react-redux";
import {
  setFullArrayOfProducts,
  setDisplayedArrayOfProducts,
  setValueByName,
} from "./redux/Shop/Shop.actions";
import { toggleSpinner } from "./redux/Spinner/Spinner.actions";
import Spinner from "./components/Spinner/spinner.component";
class App extends React.Component {
  findCorrectDateFormat(productDate) {
    let now = new Date();
    let date = "";
    if (
      now.getFullYear() === productDate.getFullYear() &&
      now.getMonth() === productDate.getMonth() &&
      now.getDate() - productDate.getDate() >= 7
    ) {
      if (now.getDate() - productDate.getDate() === 0) {
        if (now.getHours() - productDate.getHours() === 0) {
          if (now.getMinutes() - productDate.getMinutes() === 0) {
            if (now.getSeconds() - productDate.getSeconds() != 0) {
              return (date = `${
                now.getSeconds() - productDate.getSeconds()
              } minutes ago`);
            }
          } else {
            return (date = `${
              now.getMinutes() - productDate.getMinutes()
            } minutes ago`);
          }
        } else {
          return (date = `${
            now.getHours() - productDate.getHours()
          } hours ago`);
        }
      } else {
        return (date = `${now.getDate() - productDate.getDate()} days ago`);
      }
    } else {
      return (date = productDate.toString());
    }
    return date;
  }
  GetProducts = async () => {
    await this.props.toggleSpinner();
    await axios
      .get("http://localhost:5000/products/?_pages=1&_limit=80")
      .then((response) => {
        this.props.setFullArrayOfProducts(response.data);
        this.props.setDisplayedArrayOfProducts(response.data.slice(0, 20));

        this.props.setValueByName({ name: "Index", value: 20 });
      });
    await this.props.toggleSpinner();

    axios.get("http://localhost:5000/products").then((response) => {
      this.props.setFullArrayOfProducts(response.data);
    });
  };
  componentDidMount() {
    this.GetProducts();
  }
  handleScroll = async (e) => {
    const {
      Index,
      setDisplayedArrayOfProducts,
      DisplayedProductsArray,
      ProductsFullArray,
    } = this.props;
    // console.log(event.target.scrollTop);
    // console.log(event);
    if (
      e.target.scrollHeight - e.target.scrollTop === e.target.clientHeight &&
      Index <= 481
    ) {
      await this.props.setValueByName({
        name: "LoadingMore",
        value: true,
      });

      await setDisplayedArrayOfProducts(
        DisplayedProductsArray.concat(
          ProductsFullArray.slice(Index, Index + 20)
        )
      );

      await this.props.setValueByName({
        name: "Index",
        value: Index + 20,
      });
      await setTimeout(() => {
        this.props.setValueByName({
          name: "LoadingMore",
          value: false,
        });
      }, 100);

      if (
        Index <= ProductsFullArray.length &&
        Index >= ProductsFullArray.length - 20
      ) {
        setDisplayedArrayOfProducts(ProductsFullArray);
      }
      if (Index >= ProductsFullArray.length) {
        this.props.setValueByName({
          name: "NoMoreToShow",
          value: true,
        });
      }
    }
    // if (event.target.scrollTop === 261) {
    //   console.log(this.props.DisplayedProductsArray);
    //   console.log(this.props.ProductsFullArray);
    //   console.log(
    //     this.props.DisplayedProductsArray.concat(
    //       this.props.ProductsFullArray.slice(20, 40)
    //     )
    //   );
    //   this.props.setDisplayedArrayOfProducts(
    //     this.props.DisplayedProductsArray.concat(
    //       this.props.ProductsFullArray.slice(20, 40)
    //     )
    //   );
    // this.props.setDisplayedArrayOfProducts(array);
    // }
  };
  render() {
    const {
      DisplayedProductsArray,
      SpinnerVissibility,
      LoadingMore,
    } = this.props;
    return (
      <div className="Products" onScroll={this.handleScroll}>
        {SpinnerVissibility === true ? (
          <Spinner></Spinner>
        ) : (
          DisplayedProductsArray.map((Product) => {
            return (
              <div id={Product.id} className="Each-Product">
                <span style={{ fontSize: `${Product.size}px` }}>
                  {Product.face}
                </span>
                <div>{`$ ${Product.price}`}</div>
                <div>{`${this.findCorrectDateFormat(
                  new Date(Product.date)
                )}`}</div>
              </div>
            );
          })
        )}
        {LoadingMore === true ? <div>Loading more...</div> : null}
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  DisplayedProductsArray: state.shop.DisplayedProductsArray,
  ProductsFullArray: state.shop.ProductsFullArray,
  Index: state.shop.Index,
  LoadingMore: state.shop.LoadingMore,
  SpinnerVissibility: state.spinner.SpinnerVissibility,
});
const mapDispatchToProps = (dispatch) => ({
  setFullArrayOfProducts: (FullArrayOfProducts) =>
    dispatch(setFullArrayOfProducts(FullArrayOfProducts)),
  setDisplayedArrayOfProducts: (DisplayedArrayOfProducts) =>
    dispatch(setDisplayedArrayOfProducts(DisplayedArrayOfProducts)),
  toggleSpinner: () => dispatch(toggleSpinner()),
  setValueByName: (NameValue) => dispatch(setValueByName(NameValue)),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
