import React from "react";
import "./Pagination.styles.css";
const Pagination = ({ pages, clickedPage, setValueByName }) => {
  let nrofpages = [];
  for (let i = 0; i < pages; i++) {
    nrofpages.push(i + 1);
  }
  let pageCount = 0;
  return (
    <div className="pagination">
      <div className="Paginationpages Symbols">&laquo;</div>
      <div
        className="Paginationpages Symbols Lite"
        style={{ position: "relative", left: "1vw" }}
      >
        &lt;
      </div>

      {nrofpages.map((page) => {
        pageCount++;
        {
          return nrofpages.length <= 10 ? (
            <div
              className={`${
                clickedPage === page
                  ? "Paginationpages Numbers Clicked"
                  : "Paginationpages Numbers"
              }`}
              id={page}
              onClick={() => {
                setValueByName({
                  name: "clickedPage",
                  value: page,
                });
              }}
            >
              {page}
            </div>
          ) : pageCount === 1 || pageCount === 2 || pageCount === 3 ? (
            <div
              className={`${
                clickedPage === page
                  ? "Paginationpages Numbers Clicked"
                  : "Paginationpages Numbers"
              }`}
              id={page}
              onClick={() => {
                setValueByName({
                  name: "clickedPage",
                  value: page,
                });
              }}
            >
              {page}
            </div>
          ) : pageCount === 4 || pageCount === 5 ? (
            <div className="Paginationpages Points" id={page}>
              .
            </div>
          ) : clickedPage + 9 >= nrofpages.length &&
            (pageCount === 10 || pageCount === 11) ? (
            <div
              className={`${
                clickedPage === page
                  ? "Paginationpages Numbers Clicked"
                  : "Paginationpages Numbers"
              }`}
              id={page}
              onClick={() => {
                setValueByName({
                  name: "clickedPage",
                  value: page,
                });
              }}
            >
              {page}
            </div>
          ) : clickedPage + 9 >= nrofpages.length &&
            (pageCount === 12 || pageCount === 13) ? (
            <div className="Paginationpages Points" id={page}>
              .
            </div>
          ) : pageCount + 3 >= nrofpages.length ? (
            <div
              className={`${
                clickedPage === page
                  ? "Paginationpages Numbers Clicked"
                  : "Paginationpages Numbers"
              }`}
              id={page}
              onClick={() => {
                setValueByName({
                  name: "clickedPage",
                  value: page,
                });
              }}
            >
              {page}
            </div>
          ) : pageCount >= 7 + clickedPage && pageCount <= 8 + clickedPage ? (
            <div className="Paginationpages Points" id={page}>
              .
            </div>
          ) : pageCount <= 7 + clickedPage && pageCount > clickedPage ? (
            <div
              className={`${
                clickedPage === page
                  ? "Paginationpages Numbers Clicked"
                  : "Paginationpages Numbers"
              }`}
              id={page}
              onClick={() => {
                setValueByName({
                  name: "clickedPage",
                  value: page,
                });
              }}
            >
              {page}
            </div>
          ) : null;
        }
      })}

      <div
        className="Paginationpages Symbols Lite"
        style={{ position: "relative", left: "1vw" }}
      >
        &gt;
      </div>
      <div className="Paginationpages Symbols">&raquo;</div>
    </div>
  );
};

export default Pagination;
