import React from "react";
import axios from "axios";
import "./App.css";
import { connect } from "react-redux";
import {
  setFullArrayOfProducts,
  setDisplayedArrayOfProducts,
  setValueByName,
} from "./redux/Shop/Shop.actions";
import Filter from "./components/Filter/Filter.component";
import { toggleSpinner } from "./redux/Spinner/Spinner.actions";
import { setViewOfPage } from "./redux/Page/Page.actions";
import Spinner from "./components/Spinner/spinner.component";
import Product from "./components/Product/Product.component";
class App extends React.Component {
  //Make Api call and get Products Info
  GetProducts = async () => {
    //run Spinner
    await this.props.toggleSpinner();
    //start Api call
    await axios
      .get("http://localhost:5000/products/?_pages=1&_limit=80")
      .then((response) => {
        //register  80 products to redux (80 becouse no need more for start and load faster)
        this.props.setFullArrayOfProducts(response.data);
        // register products that we need to display to the user
        this.props.setDisplayedArrayOfProducts(response.data.slice(0, 20));
        //set Index to redux a variable that i use to keep truck of the product number shown to user
        this.props.setValueByName({ name: "Index", value: 20 });
      });
    //close spinner
    await this.props.toggleSpinner();
    // while user uses the pages full products will be registered not only (80)
    axios.get("http://localhost:5000/products").then((response) => {
      this.props.setFullArrayOfProducts(response.data);
    });
  };
  componentDidMount() {
    //Call function to get products when components load
    this.GetProducts();
  }
  //Handle Scrolling  to give more products
  handleScroll = async (e) => {
    const {
      Index,
      setDisplayedArrayOfProducts,
      DisplayedProductsArray,
      ProductsFullArray,
    } = this.props;

    if (
      //Check when users reach the bottom
      e.target.scrollHeight - e.target.scrollTop === e.target.clientHeight &&
      Index <= 481
    ) {
      await this.props.setValueByName({
        name: "LoadingMore",
        value: true,
      });
      // Tell message to the user in ex "Loading more"
      await this.props.setValueByName({
        name: "AddsIndexes",
        value: [...this.props.AddsIndexes, this.findRandomNotFirstAdd()],
      });
      // Set timeout so even if data come faster Loading will be shown to users for 1/3s just for design
      await setTimeout(async () => {
        await this.props.setValueByName({
          name: "Index",
          value: Index + 20,
        });
        //Get more products from full array that i use in redux state
        await setDisplayedArrayOfProducts(
          DisplayedProductsArray.concat(
            ProductsFullArray.slice(Index, Index + 20)
          )
        );
        // Keep truck of index
        await this.props.setValueByName({
          name: "Index",
          value: Index + 20,
        });
        //Stop loading more
        await this.props.setValueByName({
          name: "LoadingMore",
          value: false,
        });
        //Check if all products have been displayed
        if (
          Index <= ProductsFullArray.length &&
          Index >= ProductsFullArray.length - 20
        ) {
          setDisplayedArrayOfProducts(ProductsFullArray);
        }
      }, 300);
    }
  };
  findRandomNotFirstAdd = () => {
    let zIndex = Math.floor(Math.random() * 1000);
    while (
      zIndex == this.props.AddsIndexes[this.props.AddsIndexes.length - 1]
    ) {
      zIndex = Math.floor(Math.random() * 1000);
    }
    return zIndex;
  };
  render() {
    let ProductsCount = 0;

    const {
      DisplayedProductsArray,
      SpinnerVissibility,
      LoadingMore,
      Index,
      ProductsFullArray,
      AddsIndexes,
      ViewOfPage,
      setViewOfPage,
    } = this.props;
    return (
      <div className="filter-Products-Header">
        <div className="Filter-Header">
          <Filter />
        </div>
        <div className="SwitchView" onClick={setViewOfPage}>
          {ViewOfPage === "Pagination"
            ? "Turn on Scrolling View"
            : "Turn on Pagination View"}
        </div>

        <div className="Products" onScroll={this.handleScroll}>
          {SpinnerVissibility === true ? (
            <Spinner></Spinner>
          ) : (
            DisplayedProductsArray.map((Product) => {
              ProductsCount++;

              return ProductsCount % 20 == 0 ? (
                <div>
                  <Product
                    id={Product.id}
                    price={Product.price ? Product.price : null}
                    size={Product.size ? Product.size : null}
                    face={Product.face ? Product.face : null}
                    date={Product.date ? Product.date : null}
                    AddsIndexes={AddsIndexes ? AddsIndexes : null}
                    ProductsCount={ProductsCount ? ProductsCount : null}
                    AdsVisibility={false}
                  />
                </div>
              ) : (
                <Product
                  id={Product.id}
                  price={Product.price ? Product.price : null}
                  size={Product.size ? Product.size : null}
                  face={Product.face ? Product.face : null}
                  date={Product.date ? Product.date : null}
                  AddsIndexes={AddsIndexes ? AddsIndexes : null}
                  ProductsCount={ProductsCount ? ProductsCount : null}
                  AdsVisibility={false}
                />
              );
            })
          )}
          {LoadingMore === true ? (
            <div className="LoadingMoreend">Loading ...</div>
          ) : null}
          {Index != 0 && Index === ProductsFullArray.length ? (
            <div className="LoadingMoreend">end of catalogue</div>
          ) : null}
        </div>
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  DisplayedProductsArray: state.shop.DisplayedProductsArray,
  ProductsFullArray: state.shop.ProductsFullArray,
  Index: state.shop.Index,
  LoadingMore: state.shop.LoadingMore,
  SpinnerVissibility: state.spinner.SpinnerVissibility,
  NoMoreToShow: state.shop.NoMoreToShow,
  AddsIndexes: state.shop.AddsIndexes,
  ViewOfPage: state.page.ViewOfPage,
});
const mapDispatchToProps = (dispatch) => ({
  setFullArrayOfProducts: (FullArrayOfProducts) =>
    dispatch(setFullArrayOfProducts(FullArrayOfProducts)),
  setDisplayedArrayOfProducts: (DisplayedArrayOfProducts) =>
    dispatch(setDisplayedArrayOfProducts(DisplayedArrayOfProducts)),
  toggleSpinner: () => dispatch(toggleSpinner()),
  setValueByName: (NameValue) => dispatch(setValueByName(NameValue)),
  setViewOfPage: () => dispatch(setViewOfPage()),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
